import cv2yuz_tanima = cv2.CascadeClassifier("yuz_tanima.xml") #Cascademizi dahil ettik hangi alanda tespit yapılacaksa# onun xml dosyasını eklerizresim = cv2.imread("yuz.png")gri = cv2.cvtColor(resim,cv2.COLOR_BGR2GRAY) # tespit işlemlerindew resim binary formatda işlem göreceği# için GRİ  formattda olmalıdırtanima = yuz_tanima.detectMultiScale(gri) # detectMultiScale() fonk ile tespit işlemini gerçekleştirdikfor (x,y,genislik,yukseklik) in tanima:    cv2.rectangle(resim,(x,y),(genislik+x,yukseklik+y),(255,0,0),3) # rectangle kare çizmemizi sağladıcv2.imshow("PENCERE",resim)cv2.waitKey(0)